import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../css/sweep-mode.css";

function SweepModePage() {
  const [frequencyMin, setFrequencyMin] = useState(1); // Min frequency (Hz)
  const [frequencyMax, setFrequencyMax] = useState(50); // Max frequency (Hz)
  const [toneMin, setToneMin] = useState(frequencyMin * 10); // Min tone (Hz)
  const [toneMax, setToneMax] = useState(frequencyMax * 10); // Max tone (Hz)
  const [durationMinutes, setDurationMinutes] = useState("");
  const [intervalSeconds, setIntervalSeconds] = useState("");
  const [message, setMessage] = useState("");

  // Function to handle frequency min slider change
  const handleFrequencyMinChange = (value) => {
    setFrequencyMin(value);
    setToneMin(value); // Tone increases proportionally with frequency
  };

  // Function to handle frequency max slider change
  const handleFrequencyMaxChange = (value) => {
    setFrequencyMax(value);
    setToneMax(value); // Tone increases proportionally with frequency
  };

  // Function to handle duration change
  const startSweepMode = () => {
    if (!durationMinutes || !intervalSeconds) {
      setMessage("Please fill in all fields.");
      return;
    }
    setMessage("Sweep mode started.");
    // Implement the logic for sweep mode here
  };

  return (
    <div className="sweep-mode-container">
      <h2>Sweep Mode</h2>

      <div className="form-group">
        <label>Frequency Range (Hz):</label>
        <div className="slider-group">
          <input
            type="range"
            min="1"
            max="50"
            value={frequencyMin}
            onChange={(e) => handleFrequencyMinChange(+e.target.value)}
            className="slider"
          />
          <input
            type="range"
            min="1"
            max="50"
            value={frequencyMax}
            onChange={(e) => handleFrequencyMaxChange(+e.target.value)}
            className="slider"
          />
        </div>
        <p>{frequencyMin} Hz - {frequencyMax} Hz</p>
      </div>

      <div className="form-group">
        <label>Tone Range (Hz):</label>
        <p>{toneMin} Hz - {toneMax} Hz</p>
      </div>

      <div className="form-group">
        <label>Duration (minutes):</label>
        <input
          type="number"
          value={durationMinutes}
          onChange={(e) => setDurationMinutes(e.target.value)}
          placeholder="Enter duration in minutes"
        />
      </div>

      <div className="form-group">
        <label>Interval (seconds):</label>
        <input
          type="number"
          value={intervalSeconds}
          onChange={(e) => setIntervalSeconds(e.target.value)}
          placeholder="Enter interval in seconds"
        />
      </div>

      <button onClick={startSweepMode}>Start Sweep Mode</button>
      <p>{message}</p>

      <div className="toggle-button">
        <Link to="/static">
          <button>Go to Static Mode</button>
        </Link>
      </div>
    </div>
  );
}

export default SweepModePage;
