import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../css/static-mode.css";

function StaticModePage() {
  const [frequency, setFrequency] = useState(10); // Frequency (Hz)
  const [tone, setTone] = useState(100); // Tone (Hz)
  const [durationMinutes, setDurationMinutes] = useState("");
  const [intervalSeconds, setIntervalSeconds] = useState("");
  const [message, setMessage] = useState("");

  // Function to handle frequency slider change
  const handleFrequencyChange = (value) => {
    setFrequency(value);
    setTone(value); // Tone increases proportionally with frequency
  };

  // Function to handle tone slider change independently
  const handleToneChange = (value) => {
    setTone(value);
  };

  // Function to start static mode
  const startStaticMode = () => {
    if (!durationMinutes || !intervalSeconds) {
      setMessage("Please fill in all fields.");
      return;
    }
    setMessage("Static mode started.");
    // Implement the logic for static mode here
  };

  return (
    <div className="static-mode-container">
      <h2>Static Mode</h2>

      <div className="form-group">
        <label>Frequency (1Hz - 50Hz):</label>
        <input
          type="range"
          min="1"
          max="50"
          value={frequency}
          onChange={(e) => handleFrequencyChange(+e.target.value)}
          className="slider"
        />
        <p>{frequency} Hz</p>
      </div>

      <div className="form-group">
        <label>Tone (20Hz - 1500Hz):</label>
        <input
          type="range"
          min="20"
          max="1500"
          value={tone}
          onChange={(e) => handleToneChange(+e.target.value)}
          className="slider"
        />
        <p>{tone} Hz</p>
      </div>

      <div className="form-group">
        <label>Duration (minutes):</label>
        <input
          type="number"
          value={durationMinutes}
          onChange={(e) => setDurationMinutes(e.target.value)}
          placeholder="Enter duration in minutes"
        />
      </div>

      <div className="form-group">
        <label>Interval (seconds):</label>
        <input
          type="number"
          value={intervalSeconds}
          onChange={(e) => setIntervalSeconds(e.target.value)}
          placeholder="Enter interval in seconds"
        />
      </div>

      <button onClick={startStaticMode}>Start Static Mode</button>
      <p>{message}</p>

      <div className="toggle-button">
        <Link to="/sweep">
          <button>Go to Sweep Mode</button>
        </Link>
      </div>
    </div>
  );
}

export default StaticModePage;
