import React, { useState, useEffect } from "react";
import ReactSlider from "react-slider"; // Ensure ReactSlider is imported
import "../css/sweep-mode.css";

const SweepModePage = () => {
  const [frequencyRange, setFrequencyRange] = useState([20, 2000]); // Default frequency range
  const [currentFrequency, setCurrentFrequency] = useState(20); // Initial frequency
  const [tone, setTone] = useState(50); // Default tone value
  const [isSweeping, setIsSweeping] = useState(false); // Control sweep state
  const [useSlider, setUseSlider] = useState(true); // Toggle slider functionality

  // Sweep functionality
  useEffect(() => {
    if (!isSweeping) return;

    const interval = setInterval(() => {
      setCurrentFrequency((prevFrequency) => {
        if (prevFrequency < frequencyRange[1]) {
          return prevFrequency + 1; // Increment frequency
        } else {
          return frequencyRange[0]; // Reset to start of range
        }
      });
    }, 50); // Adjust interval speed as needed

    return () => clearInterval(interval); // Cleanup interval on component unmount or stop
  }, [isSweeping, frequencyRange]);

  // Start Sweep
  const startSweep = () => {
    setCurrentFrequency(frequencyRange[0]);
    setIsSweeping(true);
  };

  // Stop Sweep
  const stopSweep = () => {
    setIsSweeping(false);
  };

  return (
    <div className="sweep-mode-page">
      <h1>Sweep Mode</h1>

      {useSlider ? (
        <div className="slider-section">
          <h2>Set Frequencies and Tones with Sliders</h2>
          <div className="form-group">
            <label>Frequency Range (10Hz - 50Hz):</label>
            <ReactSlider
              min={10}
              max={50}
              value={frequencyRange}
              onChange={(value) => setFrequencyRange(value)}
              className="slider"
              thumbClassName="thumb"
              trackClassName="track"
              pearling
              minDistance={1} // Ensures a minimum gap between thumbs
            />
            <p>
              {frequencyRange[0]} Hz - {frequencyRange[1]} Hz
            </p>
          </div>
        </div>
      ) : null}

      {/* Tone Slider */}
      <div className="slider-container">
        <label htmlFor="tone">Tone: {tone}</label>
        <input
          type="range"
          id="tone"
          min="1"
          max="100"
          value={tone}
          onChange={(e) => setTone(Number(e.target.value))}
        />
      </div>

      {/* Current Frequency Display */}
      <p>Current Frequency: {currentFrequency} Hz</p>

      {/* Start and Stop Sweep Buttons */}
      <div className="control-buttons">
        <button onClick={startSweep} disabled={isSweeping}>
          Start Sweep
        </button>
        <button onClick={stopSweep} disabled={!isSweeping}>
          Stop Sweep
        </button>
      </div>
    </div>
  );
};

export default SweepModePage;
